SELECT d.*, p.img_path, o.ord_stat, o.create_date, o.ord_code FROM foodmate_order_detail d LEFT JOIN foodmate_order o ON d.ord_id = o.ord_id LEFT JOIN foodmate_product p ON d.pdt_id = p.pdt_id WHERE d.ORD_ID IN
#{id}
order by ord_detail_id
